import { useState, useEffect } from 'react'
import PersonsForm from './components/personForm'
import Persons from './components/persons'
import axios from 'axios'
import itemService from './services/personItems'
import PersonLine from './components/personLine'

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [personsToShow, setPersonsToShow] = useState([])

  useEffect(() => {
    axios
      .get('http://localhost:3001/persons')
      .then(response => {
        console.log('promise fulfilled')
        setPersons(response.data)
        setPersonsToShow(response.data)
      })
  }, [])
  console.log('render', persons.length, 'persons')

  const addPerson = (event) => {
    event.preventDefault()

    const entryObject = {
        name: newName,
        number: newNumber
    } 

    persons.map(
      person => person.name !== entryObject.name ? 
        itemService
          .create(entryObject)
          .then(returnedItem => {
            setPersons(persons.concat(returnedItem))
            setNewName("")
            setNewNumber("")
          })
      :
        window.confirm(`${entryObject.name} already has an entry in the phonebook. Update the number?`) 
      ) 
  }

  const handleFilterChange = (event) => {

    let _filteredPersons = persons.filter(person => 
      person.name.toLowerCase().includes(event.target.value.toLowerCase())
    )
    setPersonsToShow(_filteredPersons)
  }

  const toggleDeleteOf = (id) => {

    if (window.confirm(`Delete the entry for ${persons.find(person => person.id === id)} ?`)) {

      itemService
        .deleteItem(id)
        .then(
          setPersons(persons.map(person => person.id !== id ? person : null)),
          setPersonsToShow(persons.map(person => person.id !== id ? person : null))
        )
        .catch(error => {
          alert(
            `Something went wrong (apologies for not being more specific)`
          )
        })
    }
  }

  const handleNameChange = (event) => {
    // console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    // console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form >
            <div>
                Filter: <input onChange={handleFilterChange} />
            </div>
        </form>
      <h2>Add a new entry:</h2>
        <PersonsForm 
          addPerson = {addPerson} 
          handleNameChange = {handleNameChange}
          handleNumberChange = {handleNumberChange}
          newName = {newName}
          newNumber = {newNumber}
         /> 
      <h2>Numbers in phonebook</h2>
            <ul>
            {personsToShow.map(
                person => <PersonLine 
                  key={person.id} 
                  person={person} 
                  toggleDelete={() => toggleDeleteOf(person.id)}/>
                )
            }
          </ul>
    </div>
  )
}

export default App
