import { useState } from 'react'
import PersonsForm from './components/personForm'
import Persons from './components/persons'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '555-7658-9484', id: 1 }, 
    { name: 'Seven Cedars', number: '555-6543-2234', id: 2 }, 
    { name: 'Arto Hellas', number: '555-4231-8767', id: 3 }, 
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')

  const addPerson = (event) => {
    event.preventDefault()

    const entryObject = {
        name: newName,
        number: newNumber
    }    

    // console.log(persons.some(item => item.name === newName))

    persons.some(item => item.name === newName) ? 
        alert(`${newName} is already in the phonebook!`)
    : 
        setPersons(persons.concat(entryObject))
        setNewName("")
        setNewNumber("")
    }

  const handleNameChange = (event) => {
    // console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    // console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      ...
      <h2>Add a new entry:</h2>
        <PersonsForm 
          addPerson = {addPerson} 
          handleNameChange = {handleNameChange}
          handleNumberChange = {handleNumberChange}
          newName = {newName}
          newNumber = {newNumber}
         /> 
      <h2>Numbers in phonebook</h2>
      <Persons persons = {persons}/> 
    </div>
  )
}

export default App
